rd['state']; ?></dd>                    <dt>Zip</dt>                    <dd><?php echo $card['zip']; ?></dd>                    <dt>Country</dt>                    <dd><?php echo $card['country']; ?></dd>                    <dt>Phone</dt>                    <dd><?php echo $card['phone']; ?></dd>                </dl>                <div class="clear"></div>            </div>        </fieldset>        <fieldset>            <legend>Options</legend>            <div class="pad24t">                <p><a href="null.php"                      onclick="return delete_item('ppSD_cart_billing','<?php echo $card['id'] ?>','','','1');">Delete                        this card</a></p>            </div>        </fieldset>    </div><?php}?>