<script type="text/javascript">    $.ctrl('S', function () {        return json_add('member_types-add', 'x', '1');    });    $(function () {        $("#note_labels").sortable();        // $("#note_labels").disableSelection();    });</script><form action="" method="post" id="popupform" onsubmit="return json_add('member_types-add','x','1');">    <div id="popupsave">        <input type="button" value="Add New" onclick="return add_type();" />        <input type="submit" value="Save" class="save"/>    </div>    <h1>Membership Types</h1>    <div class="popupbody">        <p class="highlight">            Use this page to create and change your member types.        </p>        <ul id="note_labels" class="popup_longlist">            <?php            $q1 = $db->run_query("                SELECT *                FROM `ppSD_member_types`                ORDER BY `order` ASC            ");            while ($item = $q1->fetch()) {            ?>                <li id="td-cell-<?php echo $item['id'] ?>">                    <a href="null.php" onclick="return delete_item('ppSD_member_types','<?php echo $item['id'] ?>');"                       class="floatright">                        <img src="imgs/icon-delete.png" width="16" height="16" border="0" class="option_icon hover"                             alt="Delete" title="Delete"/>                    </a>                    <input type="text" name="type[<?php echo $item['id']; ?>][name]" style="width:90%;"                           value="<?php echo $item['name'] ?>"/>                    <div class="subOptions">                        <span>ID: <?php echo $item['id']; ?></span>                        <span><a href="null.php" onclick="return switch_popup('member_types-edit','id=<?php echo $item['id']; ?>');">Edit Content Package</a></span>                    </div>                    <input type="hidden" name="type[<?php echo $item['id']; ?>][id]" value="<?php echo $item['id'] ?>"/>                </li>            <?php            }            ?>        </ul>    </div></form><script type="text/javascript">    function add_type() {        show_loading();        send_data = 'action=member_type_entry';        $.post('cp-functions/event_addition.php', send_data, function (theResponse) {            $('#note_labels').append(theResponse);            close_loading();        });        return false;    }</script>