<?phpif (!empty($_POST['id'])) {    $editing  = '1';    $final_id = $_POST['id'];    $history  = new history($final_id, '', '', '', '', '', 'ppSD_login_announcements');    $active   = $history->final_content['active'];    $title    = $history->final_content['title'];    $starts   = $history->final_content['starts'];    $ends     = $history->final_content['ends'];    $content  = $history->final_content['content'];} else {    $editing  = '0';    $final_id = '';    $active   = '1';    $title    = '';    $starts   = '';    $ends     = '';    $content  = '';}?><script type="text/javascript">    $.ctrl('S', function () {        return json_add('announcement-add', '<?php echo $final_id; ?>', '<?php echo $editing; ?>', 'popupform');    });</script><form action="" method="post" id="popupform"      onsubmit="return json_add('announcement-add','<?php echo $final_id; ?>','<?php echo $editing; ?>','popupform');">    <div id="popupsave">        <input type="submit" value="Save" class="save"/>    </div>    <h1>Add Announcement</h1>    <div class="pad24t popupbody">        <p class="highlight">Announcements are news postings which appear on the member's dashboard after he/she logs in. These are not e-mail or SMS broadcasts. Please use the "Connect" tab for that functionality.</p>        <fieldset>            <legend>Overview</legend>            <div class="pad24t">                <div class="field">                    <label class="top">Status</label>                    <div class="field_entry_top">                        <input type="radio" name="active" value="1" <?php if ($active == '1') {                            echo " checked=\"checked\"";                        } ?> /> Active <input type="radio" name="active" value="0" <?php if ($active != '1') {                            echo " checked=\"checked\"";                        } ?> /> Inactive                    </div>                </div>                <div class="field">                    <label class="top">Title</label>                    <div class="field_entry_top">                        <input type="text" name="title" style="width:400px;" class="req" value="<?php echo $title; ?>"/>                    </div>                </div>                <div class="col50l">                    <div class="field">                        <label class="top">Starts</label>                        <div class="field_entry_top">                            <?php                            echo $admin->datepicker('starts', $starts, '1');                            ?>                            <p class="field_desc">When should this announcement first appear?</p>                        </div>                    </div>                </div>                <div class="col50r">                    <div class="field">                        <label class="top">Ends</label>                        <div class="field_entry_top">                            <?php                            echo $admin->datepicker('ends', $ends, '1');                            ?>                            <p class="field_desc">When should this announcement stop appearing?</p>                        </div>                    </div>                </div>                <div class="clear"></div>            </div>        </fieldset>        <fieldset>            <legend>Content</legend>            <div class="pad24t">                <textarea name="content" class="richtext" id="event_rich"                          style="width:100%;height:400px;"><?php echo $content; ?></textarea>                <?php                echo $admin->richtext('100%', '400px', 'event_rich');                ?>            </div>        </fieldset>    </div></form>