');">    <div id="popupsave">        <?php        if (! empty($_POST['id'])) {            ?>            <input type="submit" value="Grant Access From Criteria" onclick="return switch_popup('build_criteria','type=member&act=content_access&act_id=<?php echo $cid; ?>','');" />        <?php        }        ?>        <input type="submit" value="Save" class="save"/>        <input type="hidden" name="type" value="<?php echo $type; ?>"/>    </div>    <h1>Redirection Management</h1>    <div class="pad24t popupbody">        <fieldset>            <legend>Basic Details</legend>            <div class="pad24t">                <div class="field">                    <label>Name</label>                    <div class="field_entry">                        <input type="text" name="name" class="req" id="name" style="width:300px;"                               value="<?php echo $data['name']; ?>"/>                    </div>                </div>                <div class="field">                    <label>Permalink</label>                    <div class="field_entry">                        <input type="text" onkeyup="return check_permalink();" name="permalink" class="req"                               id="permalink" style="width:300px;" value="<?php echo $data['permalink_clean']; ?>"/>                        <p class="field_desc" id="preview_permalink"><?php echo $data['full_link']; ?></p>                    </div>                </div>                <div class="field">                    <label>Redirect To</label>                    <div class="field_entry">                        <input type="text" placeholder="http://" name="url" class="req" id="url" style="width:100%;"                               value="<?php echo $data['url']; ?>"/>                    </div>                </div>            </div>        </fieldset>        <fieldset>            <legend>Options</legend>            <div class="pad24t">                <div class="field">                    <label>Add to Menu?</label>                    <div class="field_entry">                        <?php                        echo $admin->get_menus($data['menus']);                        ?>                    </div>                </div>            </div>        </fieldset>    </div></form><script type="text/javascript">    function check_permalink() {        send_data = 'section=&id=<?php echo $cid; ?>&permalink=' + $('#permalink').val();        $.post('cp-functions/preview_permalink.php', send_data, function (theResponse) {            $('#preview_permalink').html(theResponse);        });        return false;    }</script>