                        <dd><?php echo $details['date_show']; ?></dd>                        <dt>Conversion Time</dt>                        <dd><?php echo $details['time_to_convert']; ?></dd>                        <dt>Converted By</dt>                        <dd><?php $employee = $admin->get_employee('', $details['owner']);                            echo $employee['username']; ?></dd>                    </dl>                    <div class="clear"></div>                </div>            </fieldset>            <fieldset>                <legend>Value</legend>                <div class="pad24t">                    <dl>                        <dt>Expected Value</dt>                        <dd><?php echo $details['estimated_formatted']; ?></dd>                        <dt>Actual Value</dt>                        <dd><?php echo $details['actual_formatted']; ?></dd>                        <dt>Difference</dt>                        <dd><?php echo $details['difference']; ?></dd>                        <dt>Change</dt>                        <dd><?php                            if ($details['percent_change'] > 0) {                                echo '<div class="positive">+' . $details['percent_change'] . '%</div>';                            } else if ($details['percent_change'] < 0) {                                echo '<div class="negative">-' . $details['percent_change'] . '%</div>';                            } else {                                echo '=';                            }                            ?></dd>                    </dl>                    <div class="clear"></div>                </div>            </fieldset>        </div>        <div class="clear"></div>        </div>    <?php    }}?>