ce() {        var math = Math.random().toString(36).substring(7);        html = '<li><input type="text" name="pipeline[new_' + math + '][name]" style="width:90%;" value="" /><input type="hidden" name="source[new_' + math + '][id]" value="new" /></li>';        $('#source_list').append(html);        return false;    }</script>